import React, { Component } from 'react';
import axios from "axios";
import { FormGroup, ControlLabel, FormControl, HelpBlock, Button, Table } from 'react-bootstrap';

const rSItems = require('../assets/rs_items.json');

function FieldGroup({ id, label, help, ...props }) {
  return (
    <FormGroup controlId={id}>
    <ControlLabel>{label}</ControlLabel>
    <FormControl {...props} />
    {help && <HelpBlock>{help}</HelpBlock>}
    </FormGroup>
  );
}

class BestMargins extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      totalGold: '',
      item: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.itemInformation = this.itemInformation.bind(this);
    this.itemRow = this.itemRow.bind(this);

  }

  itemRow(item) {
    return (
      <tr>
      <td>{ this.state.item.id }</td>
      <td>{ this.state.item.name }</td>
      <td>{ this.state.item.storePrice }</td>
      <td>{ this.state.item.sellPriceAv }</td>
      <td>{ this.state.item.buyPriceAv }</td>
      <td>{ this.state.item.buyQ }</td>
      <td>{ this.state.item.sellQ }</td>
      </tr>
    );
  }

  async handleSubmit(e) {
    e.preventDefault();
    // //console.log(this.state.name);
    console.log("HANDLESUBMIT")
    this.itemInformation();
  }

  async itemInformation() {
    const res = await axios.post('/runescape/items', {});
    //console.log(res);
    const body = await res.data;
    console.log(body);
    this.setState({ items: body }, () => {});
  }

  render() {
    if(this.state.items) {
      return (
        <div>
        <form onSubmit={this.handleSubmit}>
        <FieldGroup
        id="formControlsTotalGold"
        type="text"
        label="Total Gold"
        name="totalGold"
        placeholder="Enter Gold"
        value={this.state.totalGold}
        onChange={this.handleChange}
        />
        <Button type="submit" bsStyle="primary">Submit</Button>
        </form>
        <Table striped bordered condensed hover responsive>
        <thead>
        <tr>
        <th>#</th>
        <th>Name</th>
        <th>Store Price</th>
        <th>Buy Price</th>
        <th>Sell Price</th>
        <th>Buy Quantity</th>
        <th>Sell Quantity</th>
        </tr>
        </thead>
        <tbody>
        {this.state.items.map((item) => this.itemRow(item))}
        </tbody>
        </Table>
        </div>
      );
    }
    else{
      return(<div></div>);
    }
  }
}

export default BestMargins;
