import React, { Component } from 'react';
import axios from "axios";
import { FormGroup, ControlLabel, FormControl, HelpBlock, Button } from 'react-bootstrap';

function FieldGroup({ id, label, help, ...props }) {
  return (
    <FormGroup controlId={id}>
      <ControlLabel>{label}</ControlLabel>
      <FormControl {...props} />
      {help && <HelpBlock>{help}</HelpBlock>}
    </FormGroup>
  );
}
class SingleItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      itemID: '',
      item: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.itemInformation = this.itemInformation.bind(this);
    this.handleChange = this.handleChange.bind(this);

  }

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    this.itemInformation();
  }

  async itemInformation() {
    const res = await axios.post('/runescape/item', {itemID: this.state.itemID});
    const body = await res.data;
    console.log(body);
    this.setState({ item: body });
  }

  render() {
    return (
      <div>
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <FieldGroup
            id="formControlsItemID"
            type="text"
            value={this.state.value}
            placeholder="Enter text"
            onChange={this.handleChange}
            name="itemID"
            label="Item ID"
            />
          <Button type="submit" bsStyle="primary">Submit</Button>
        </form>
        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}

export default SingleItem;
