import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from "axios";
export const SEND_TEXT = 'send_text';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      itemId: '',
      item: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.itemInformation = this.itemInformation.bind(this);

  }

  handleSubmit(e) {
    e.preventDefault();
    this.itemInformation();
  }

  async itemInformation() {
    const res = await axios.post('/runescape/item', {itemId: this.state.itemId});
    const body = await res.data;
    console.log(body);
    this.setState({ item: body });
  }

  render() {
    return (
      <div className="App">
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
          <input
            type="text"
            value={this.state.post}
            onChange={e => this.setState({ post: e.target.value })}
            />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}

export default App;
